css:
*{
    font-family: "Times New Roman";
    font-size: 12;
    font: black;
}
table{
    justify-content: center;
    align-items: center;
    border: 1px solid black;
}
tr{
    border: 1px solid black;
}
td{
    border: 1px solid black;
}
th{
    color: black;
    border: 1px solid black;
}
.table-bordered td, .table-bordered th {
    border: 1px solid black !important; /* Keeps individual cell borders */
}

.table-bordered {
    border: 2px solid #828282 !important; /* Adjust thickness */

}

.black-background {
    background-color: #f2eeed;
}
