css:
table {
	max-width: 100%;
	margin: 0 auto;
    width: 100%;
    text-align:justify;
    font-size:small;
}
th{
    background:#E6E6E6;
    color:black;
    font-size:12px;
}
th, td {
        border: 1px solid black; /* Adds border to all table cells */
        padding: 8px;
}
#col{
    border:1px solid #CBCBCB;
	border-collapse: collapse;
	font-size:12px;
	text-align: center;
}
