css:
.table-bordered {
    border: 1px solid black !important; /* Adjust thickness as needed */
    font-size: 13px;
    border-radius: 6px; /* Adjust the radius as needed */
    border-collapse: separate; /* This is needed for border-radius to work properly */
    overflow: hidden; /* This will ensure the content doesn't overflow outside the rounded corners */
}
.table-bordered td, .table-bordered th {
    border: 0.1px solid grey !important;
}
.no-border td {
        border: none !important;
}

.black-background {
    background-color: #f2eeed;
}
